package lec1;

public class intro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//bitwise operators are much faster than logical operators
		//every number can be made as the sum of unique powers of 2
		
		int n = 67;
		if((n&1)==0)
			System.out.println("even");
		else
			System.out.println("odd");
		
		
		//left-shift and right-shift
		
		
	}

}
